import * as React from 'react';
import toolkitstyles from '../styles.module.scss';

const FormDraftButton = props => {
  if (!props.draftButtonText || !props.onDraft) {
    return null;
  }

  return React.createElement("button", {
    type: "button",
    formNoValidate: true,
    className: `${toolkitstyles.form__buttonwrapper__button} ${props.draftButtonClasses ? props.draftButtonClasses : 'atom_actionbutton atom_actionbutton--secondary'}`,
    onClick: () => {
      props.onFormSubmit(() => {
        if (props.onDraft) {
          props.onDraft();
        }
      });
    },
    "data-testid": props.draftButtonTestId
  }, props.draftButtonText);
};

export default FormDraftButton;
