export const sizeIsValid = (file, size) => {
  if (file.size > size) {
    return false;
  }

  return true;
};
export const typeIsValid = (file, validFileTypes) => {
  if (Array.isArray(validFileTypes)) {
    if (validFileTypes.indexOf(file.type) < 0) {
      return false;
    }
  } else if (validFileTypes !== file.type) {
    return false;
  }

  return true;
};
export const mimeTypeIsValid = (file, validMimeTypes) => {
  if (validMimeTypes.indexOf(file.type) < 0) {
    return false;
  }

  return true;
};
